import com.Utils;

/**
 * [每日一题] 2024-08-06 10:33:24
 * 题目名称: 恢复数组
 * 题目类型:
 * 思考:
 */
public class Code1416_restoreTheArray {
    public static void main(String[] args) {
        Solution solution = new Code1416_restoreTheArray().new Solution();

        int i = solution.numberOfArrays("", 1000000000);

        System.out.println(i);
    }

    //leetcode submit region begin(Prohibit modification and deletion)
    class Solution {
        public int numberOfArrays(String s, int k) {

            long[] dp = new long[s.length() + 1];

            int kLength = String.valueOf(k).length();

            dp[0] = 1;
            for (int i = 1; i < dp.length; i++) {
                long beforeNum = 0;
                for (int j = i; j >= 1; j--) {
                    long num = (s.charAt(j - 1) - '0') * (long) Math.pow(10, i - j) + beforeNum;
                    beforeNum = num;
                    if (num > k || i - j > kLength) {
                        break;
                    }
                    if (s.charAt(j - 1) != '0') {
                        dp[i] += dp[j - 1];
                        dp[i] %= 1_000_000_007;
                    }
                }
            }


//            Utils.printTable(dp);

            return (int) (dp[dp.length - 1] % 1_000_000_007);


        }
    }
//leetcode submit region end(Prohibit modification and deletion)

}
