    class Solution {
        public int minTimeToReach(int[][] moveTime) {
            moveTime[0][0] = 0;

            PriorityQueue<NodeInfo> pq = new PriorityQueue<>(Comparator.comparingInt(o -> o.waitTime));


            pq.add(new NodeInfo(0, 0, moveTime[0][0]));

            boolean[][] visited = new boolean[moveTime.length][moveTime[0].length];

            while (!pq.isEmpty()) {
                NodeInfo poll = pq.poll();
//                System.out.println(pq.size());

                int[][] directions = {{-1, 0}, {1, 0}, {0, 1}, {0, -1}};

                if (visited[poll.x][poll.y]){
                    continue;
                }

                visited[poll.x][poll.y] = true;
                if (poll.x == moveTime.length - 1 && poll.y == moveTime[0].length - 1) {
                    return poll.waitTime;
                }

                for (int[] dir : directions) {
                    int nextX = poll.x + dir[0];
                    int nextY = poll.y + dir[1];

                    if (nextX < 0 || nextY < 0 || nextX >= moveTime.length || nextY >= moveTime[0].length) {
                        continue;
                    }

                    if (visited[nextX][nextY]) {
                        continue;
                    }

                    int comeTime = Math.max(poll.waitTime, moveTime[nextX][nextY]) + 1;
                    pq.add(new NodeInfo(nextX, nextY, comeTime));

                }


            }

            return -1;

        }

        public class NodeInfo {
            public int x;
            public int y;
            public int waitTime;

            public NodeInfo(int x, int y, int waitTime) {
                this.x = x;
                this.y = y;
                this.waitTime = waitTime;
            }

            @Override
            public String toString() {
                return "NodeInfo{" +
                        "x=" + x +
                        ", y=" + y +
                        ", waitTime=" + waitTime +
                        '}';
            }
        }
    }
